        // for (let i in employee.assSur){
        //     empAss.push(employee.assSur[i])
        // }

        // for (let i in sur){
        //     if (empAss.includes(sur[i].sid)){
        //         assSur.push(sur[i])
        //     }
        // }

        // for (let i in sur){
        //     if (!empAss.includes(sur[i].sid)){
        //         surcopy.push(sur[i])
        //     }
        // }

        // this.setState({
        //     empSur:surcopy,
        //     empAss:assSur
        // })

    }


    render(){
    return(
        <div>
            <div className="columns is-centered">
                <div className="employee has-text-centered">
                <h3>Select Employee</h3>
                    <div className="select">
                        <select onChange={this.changeEmp} value={this.state.value}>
                            <option value={-1}>Select Employee</option>
                            { this.props.employees.map((employee,i) => {
                                return <option key ={i} value={i}>{employee.name}</option>
                            })}
                        </select>
                        </div>
                </div>
            </div>

            <Survey empSur={this.state.empSur} 
                    empAss={this.state.empAss}
                    employees={this.props.employees}
                    employee ={this.props.employees[this.state.value]}/>

            <div className="has-text-centered is-centered">
                <button className="button is-success">Done</button>
            </div>
        </div>
    )
    }
}

const mapStateToProps = state => {
    return {
        employees: state.employee.employee,
        surveys: state.survey.surveys,
        assSurvey: state.survey.assSur,
        empSur: state.employee.empSur,
        empAss: state.empl
    }
}

const mapDispatchToProps = dispatch => {
    return {

        fetchEmployees: () => dispatch(fetchEmployee()),
        fetchSurveys: () => dispatch(fetchSurvey()),
        changeEmployees: (indx, surveys) => dispatch(changeEmployee(indx, surveys))
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Employee);
