import React from 'react';
import Survey from '../survey/survey';
import axios from 'axios';
import { connect } from 'react-redux';
import { fetchEmployee } from '../../redux'
import { fetchSurvey } from '../../redux'

let assSurveys=[]

class Employee extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            surveysList: [],
            assSurveys : assSurveys,
            empSur:[],
            empAss:[],
            value : -1
        }
        this.changeEmp = this.changeEmp.bind(this)
    }

    componentDidMount(){
        this.props.fetchEmployees()

        axios.get('http://localhost:7000/surveys')
        .then(res => this.setState({ surveysList: res.data}))
        .catch(err => console.log(err))
    }

    changeEmp(e){

        this.setState({ value: e.target.value})
        if(e.target.value == -1){
            this.setState({
                empSur: [],
                empAss: []
            })
            return
        }

        const employee = this.props.employees[e.target.value]
        let sur = [...this.state.surveysList]
        let surcopy = []
        let assSur=[]
        let empAss = [] 

        for (let i in employee.assSur){
            empAss.push(employee.assSur[i])
        }

        for (let i in sur){
            if (empAss.includes(sur[i].sid)){
                assSur.push(sur[i])
            }
        }

        for (let i in sur){
            if (!empAss.includes(sur[i].sid)){
                surcopy.push(sur[i])
            }
        }

        this.setState({
            empSur:surcopy,
            empAss:assSur
        })

    }


    render(){
    return(
        <div>
            <div className="columns is-centered">
                <div className="employee has-text-centered">
                <h3>Select Employee</h3>
                    <div className="select">
                        <select onChange={this.changeEmp} value={this.state.value}>
                            <option value={-1}>Select Employee</option>
                            { this.props.employees.map((employee,i) => {
                                return <option key ={i} value={i}>{employee.name}</option>
                            })}
                        </select>
                        </div>
                </div>
            </div>

            <Survey empSur={this.state.empSur} 
                    empAss={this.state.empAss}
                    employees={this.props.employees}
                    employee ={this.props.employees[this.state.value]}/>

            <div className="has-text-centered is-centered">
                <button className="button is-success">Done</button>
            </div>
        </div>
    )
    }
}

const mapStateToProps = state => {
    return {
        employees: state.employee.employee
    }
}

const mapDispatchToProps = dispatch => {
    return {

        fetchEmployees: () => dispatch(fetchEmployee()),
        fetchSurveys: () => dispatch(fetchSurvey())
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Employee);
