                    ...state,
                    empSur:[],
                    empAss:[],
                    value: action.payload.indx
                }
            }
            const employee = state.employee[action.payload.indx]
            console.log(action.payload)
            let sur = [...action.payload.surveys]
            let surcopy = []
            let assSur=[]
            let empAss = [] 

            for (let i in employee.assSur){
                empAss.push(employee.assSur[i])
            }

            for (let i in sur){
                if (empAss.includes(sur[i].sid)){
                    assSur.push(sur[i])
                }
            }

            for (let i in sur){
                if (!empAss.includes(sur[i].sid)){
                    surcopy.push(sur[i])
                }
            }

            return { 
                ...state,
                empSur:surcopy,
                empAss:assSur,
                value: action.payload.indx
            }
        case ASSIGN_EMPLOYEE_SURVEY:
            const  sid  = action.payload
            while (indx < state.empSur.length){
                if (state.empSur[indx].sid === sid){
                    console.log(indx)
                    newSurvey = state.empSur[indx]
                }
                indx+=1
            }
            newAss = [...state.empAss, newSurvey]
            let newSurList = state.empSur.filter(survey => {
                return survey.sid!==sid
            })

            const newEmpAss = [...state.employee[state.value].assSur, sid]
            let newEmployees = [...state.employee]
            newEmployees[state.value].assSur = newEmpAss

            return{

                ...state,
                employee: newEmployees,
                empSur: newSurList,
                empAss: newAss

            }
        case REMOVE_EMPLOYEE_SURVEY:
            for (indx in this.props.empAss){
                if (this.props.empAss[indx].sid === sid){
                    newSurvey = this.props.empAss[indx]
                }
            }
            let newSurrList = [...this.props.empSur, newSurvey]
            newAss = this.props.empAss.filter(survey=>{
            return survey.sid!==sid
            })

            this.setState({
                surveys: newSurrList,
                assignedSurveys: newAss})

            const newAssSur = this.props.employees[this.props.value].assSur.filter(assSid=>{
                return assSid !== sid;
            })
            let newEmployee = {}
            newEmployee = Object.assign(newEmployee, this.props.employees[this.props.value])
            newEmployee.assSur = newAssSur
            this.setState({
                employee: newEmployee
            })

            return state

        default: 
            return state
    }
}

export default empReducer;
