import {FETCH_EMPLOYEE_REQUEST,
    FETCH_EMPLOYEE_SUCCESS,
    FETCH_EMPLOYEE_ERROR,
    CHANGE_EMPLOYEE} 
from './employeeTypes';

const initialState = {
    loading: false,
    employee:[],
    empSur:[],
    empAss:[],
    err:''
}

const empReducer = (state = initialState, action) =>{

    switch(action.type){
        case FETCH_EMPLOYEE_REQUEST:
            console.log("reducer 1")
            return {
                ...state,
                loading: true
            }

        case FETCH_EMPLOYEE_SUCCESS:
            console.log("reducer 2")
            return {
                ...state,
                employee: action.payload,
                loading: true
            }
        case FETCH_EMPLOYEE_ERROR:
            console.log("reducer 3")
            return {
                ...state,
                loading: false,
                err: action.payload
            }
        case CHANGE_EMPLOYEE:

            this.setState({ value: e.target.value})
        if(e.target.value == -1){
            this.setState({
                empSur: [],
                empAss: []
            })
            return
        }

        const employee = this.props.employees[e.target.value]
        let sur = [...this.props.surveys]
        let surcopy = []
        let assSur=[]
        let empAss = [] 

        for (let i in employee.assSur){
            empAss.push(employee.assSur[i])
        }

        for (let i in sur){
            if (empAss.includes(sur[i].sid)){
                assSur.push(sur[i])
            }
        }

        for (let i in sur){
            if (!empAss.includes(sur[i].sid)){
                surcopy.push(sur[i])
            }
        }

        this.setState({
            empSur:surcopy,
            empAss:assSur
        })

        default: 
        console.log("reducer 4")
            return state
    }
}

export default empReducer;
