import {FETCH_EMPLOYEE_REQUEST,
    FETCH_EMPLOYEE_SUCCESS,
    FETCH_EMPLOYEE_ERROR} 
from './employeeTypes';

const initialState = {
    loading: false,
    employee:[],
    empSur:[],
    empAss:[],
    err:''
}

const empReducer = (state = initialState, action) =>{

    switch(action.type){
        case FETCH_EMPLOYEE_REQUEST:
            console.log("reducer 1")
            return {
                ...state,
                loading: true
            }

        case FETCH_EMPLOYEE_SUCCESS:
            console.log("reducer 2")
            return {
                ...state,
                employee: action.payload,
                loading: true
            }
        case FETCH_EMPLOYEE_ERROR:
            console.log("reducer 3")
            return {
                ...state,
                loading: false,
                err: action.payload
            }

        default: 
        console.log("reducer 4")
            return state
    }
}

export default empReducer;
