import React from 'react';
import AssignedSurveys from './assigned_surveys';
import SurveyList from './survey_list';
import { connect } from 'react-redux';
import { assignEmployeeSurvey  } from '../../redux'

class Survey extends React.Component{

    constructor(props){
        super(props);
        this.assignSurvey = this.assignSurvey.bind(this)
        this.removeSurvey = this.removeSurvey.bind(this)
    }

    // componentDidUpdate(prevProps){
    //     if(this.props !== prevProps){
    //      this.setState({
    //       surveys:this.props.empSur,
    //       assignedSurveys: this.props.empAss,
    //       employees: this.props.employees,
    //       employee: this.props.employee
    //      })
    //     }
    // }

    assignSurvey(sid){

        console.log("ass")
        this.props.assignEmpSurvey(sid)
        // let newSurvey = ""
        // var indx
        // for (indx in this.props.empSur){
        //     if (this.props.empSur[indx].sid === sid){
        //         newSurvey = this.props.empSur[indx]
        //     }
        // }
        // let newAss = [...this.props.empAss, newSurvey]
        // let newSurList = this.props.empSur.filter(survey=>{
        //    return survey.sid!==sid
        // })

        // this.setState({
        //     surveys: newSurList,
        //     assignedSurveys: newAss})

        // this.props.employees[this.props.value].assSur.push(sid)

    }

    removeSurvey(sid){
        let newSurvey = ""
        var indx
        for (indx in this.props.empAss){
            if (this.props.empAss[indx].sid === sid){
                newSurvey = this.props.empAss[indx]
            }
        }
        let newSurrList = [...this.props.empSur, newSurvey]
        let newAss = this.props.empAss.filter(survey=>{
           return survey.sid!==sid
        })

        this.setState({
            surveys: newSurrList,
            assignedSurveys: newAss})

        const newAssSur = this.this.props.employees[this.props.value].assSur.filter(assSid=>{
            return assSid !== sid;
        })
        let newEmployee = {}
        newEmployee = Object.assign(newEmployee, this.this.props.employees[this.props.value])
        newEmployee.assSur = newAssSur
        this.setState({
            employee: newEmployee
        })

    }

    render(){
        return(
            <div className="columns">
                    <SurveyList surveys={this.props.empSur} addSurvey = {this.assignSurvey}/>
                    <AssignedSurveys assSurveys={this.props.empAss} removeSurvey={this.removeSurvey}/>

            </div>
        )
    }
}

const mapStateToProps = state => {
    return {
        employees: state.employee.employee,
        empSur: state.employee.empSur,
        empAss: state.employee.empAss,
        value: state.employee.value
    }
}

const mapDispatchToProps = dispatch => {
    return {

        assignEmpSurvey: (sid) => dispatch(assignEmployeeSurvey(sid)),
    }
}

export default connect(mapStateToProps,mapDispatchToProps)(Survey)
